settings.custom_css:
nav ul.elementor-nav-menu li.sign-in.menu-item {
    background-color: #fff;
}

nav ul.elementor-nav-menu li.sign-in.menu-item a.elementor-item.elementor-item-anchor {
    color: #0C1A81;
    font-family: Open Sans;
    font-size: 16px;
    font-style: normal;
    font-weight: 800;
    line-height: 19px;
    letter-spacing: 1.6px;
    text-transform: uppercase;
}


nav ul.elementor-nav-menu li.language-link.menu-item a i {
    font-size:24px !important;
}

nav ul.elementor-nav-menu li.user-profile.menu-item a i {
    font-size:24px !important;
}

@media all and (max-width: 1152px) {
    nav ul.elementor-nav-menu {
        display: flex;
        flex-direction: column;
        align-items: center;
        width: 100%;
    }

    nav ul.elementor-nav-menu li.sign-in.menu-item {
        order: -1;
        background-color: #EFF0FD;
    }

    nav ul.elementor-nav-menu li.user-profile.menu-item {
        order: -1;
    }

    nav ul.elementor-nav-menu li.sign-in.menu-item a.elementor-item.elementor-item-anchor {
        color: #0C1A81;
    }

    nav.elementor-nav-menu--dropdown.elementor-nav-menu__container ul.elementor-nav-menu {
        background-color: #fff;
    }

    nav.elementor-nav-menu--dropdown.elementor-nav-menu__container ul.elementor-nav-menu li.menu-item {
        display: flex;
        align-items: center;
        align-self: stretch;
        background: #0C1A81;
        box-shadow: 0px 1px 0px 0px #E2E8F0;
    }

    nav.elementor-nav-menu--dropdown.elementor-nav-menu__container ul.elementor-nav-menu li.menu-item:not(:last-child) {
        border-bottom: 1.5px solid #fff;
    }

    nav.elementor-nav-menu--dropdown ul.elementor-nav-menu li.menu-item a.elementor-item {
        color: #fff;
        font-family: Open Sans;
        font-size: 16px;
        font-style: normal;
        font-weight: 800;
        line-height: 19px;
        letter-spacing: 1.6px;
        text-transform: uppercase;
        width: 100%;
    }

    nav.elementor-nav-menu--dropdown.elementor-nav-menu__container ul.elementor-nav-menu li.menu-item.menu-item-has-children {
        display: flex;
        flex-direction: column;
    }

    nav.elementor-nav-menu--dropdown.elementor-nav-menu__container ul.elementor-nav-menu li.menu-item.menu-item-has-children a {
        display: flex;
        justify-content: space-between;
        align-items: center;
        width: 100%;
    }

    nav.elementor-nav-menu--dropdown.elementor-nav-menu__container ul.elementor-nav-menu li.menu-item.menu-item-has-children a.highlighted {
        background-color: #0C1A81;
        color: #fff;
    }

    nav.elementor-nav-menu--dropdown.elementor-nav-menu__container ul.elementor-nav-menu li.menu-item.menu-item-has-children ul {
        width: 100% !important;
    }

    nav.elementor-nav-menu--dropdown.elementor-nav-menu__container ul.elementor-nav-menu li.menu-item.menu-item-has-children ul li {
        background-color: #fff;
    }

    nav.elementor-nav-menu--dropdown.elementor-nav-menu__container ul.elementor-nav-menu li.menu-item.menu-item-has-children ul li:not(:last-child) {
        border-bottom: 2px solid #0C1A81;
    }
}
